// Keep track of the currently playing audio and playback settings
let currentAudio = null;
let currentIndex = -1;
let isLooping = false;
let isQueueing = false;
let originalSongList = [...songs];

// Function to format time in MM:SS format
function formatTime(seconds) {
    const minutes = Math.floor(seconds / 60);
    const secs = Math.floor(seconds % 60);
    return `${minutes}:${secs < 10 ? '0' : ''}${secs}`;
}

// Function to stop all other audios when a new one is played
function stopAllAudios(exceptThisAudio) {
    const allAudios = document.querySelectorAll('audio');
    allAudios.forEach(audio => {
        if (audio !== exceptThisAudio) {
            audio.pause();
            audio.currentTime = 0;
            const playBtn = audio.parentElement.querySelector('.play-btn');
            if (playBtn) {
                playBtn.textContent = 'Play';
            }
            const playtimeBar = audio.parentElement.querySelector('.playtime-bar');
            if (playtimeBar) {
                playtimeBar.style.width = '0%';
            }
            const timeDisplay = audio.parentElement.querySelector('.time-display');
            if (timeDisplay) {
                timeDisplay.textContent = '0:00';
            }
        }
    });
}

// Function to populate the song list
function loadSongs(songArray = songs) {
    const songList = document.getElementById('songList');
    songList.innerHTML = ''; // Clear existing list
    songArray.forEach((song, index) => {
        const songBar = document.createElement('div');
        songBar.classList.add('song-bar');
        songBar.innerHTML = `
            <div class="song-info">
                <div class="song-details">
                    <h3>${song.name}</h3>
                    <p>${song.singer}</p>
                </div>
                <div class="playtime-container">
                    <div class="playtime-bar-container" onmousedown="seekSong(${index}, event)" ontouchstart="seekSong(${index}, event)">
                        <div class="playtime-bar" id="playtime-bar-${index}"></div>
                    </div>
                    <div class="time-display" id="time-display-${index}">0:00</div>
                </div>
            </div>
            <div class="song-controls">
                <button class="control-btn play-btn" onclick="togglePlay(${index}, this)">Play</button>
                <button class="control-btn lyrics-btn" onclick="showLyrics(${index})">Lyrics</button>
                <a href="${song.videoLink}" target="_blank">
                    <button class="control-btn video-btn">Video</button>
                </a>
                <div class="volume-control">
                    <span class="volume-icon">🔊</span>
                    <div class="volume-bar-container">
                        <div class="volume-bar" id="volume-bar-${index}"></div>
                        <input type="range" min="0" max="1" step="0.01" value="0.5" oninput="adjustVolume(${index}, this.value)">
                    </div>
                </div>
                <audio id="audio-${index}" src="${song.audioSrc}"></audio>
            </div>
        `;
        songList.appendChild(songBar);
    });
}

// Function to toggle play/pause for a song
function togglePlay(index, button) {
    const audio = document.getElementById(`audio-${index}`);
    const playtimeBar = document.getElementById(`playtime-bar-${index}`);
    const timeDisplay = document.getElementById(`time-display-${index}`);
    
    // If there's a currently playing audio and it's not this one, stop it
    if (currentAudio && currentAudio !== audio) {
        stopAllAudios(audio);
    }

    if (audio.paused) {
        audio.play();
        button.textContent = 'Pause';
        currentAudio = audio;
        currentIndex = index;

        // Update playtime bar and countdown
        audio.ontimeupdate = () => {
            const progress = (audio.currentTime / audio.duration) * 100;
            playtimeBar.style.width = `${progress}%`;
            const remainingTime = audio.duration - audio.currentTime;
            timeDisplay.textContent = formatTime(remainingTime);
        };

        // Handle audio end
        audio.onended = () => {
            button.textContent = 'Play';
            playtimeBar.style.width = '0%';
            timeDisplay.textContent = '0:00';
            currentAudio = null;
            currentIndex = -1;

            if (isLooping) {
                togglePlay(index, button); // Replay the same song
            } else if (isQueueing && index < songs.length - 1) {
                const nextIndex = index + 1;
                const nextButton = document.querySelectorAll('.play-btn')[nextIndex];
                togglePlay(nextIndex, nextButton); // Play the next song
            }
        };
    } else {
        audio.pause();
        button.textContent = 'Play';
        currentAudio = null;
        currentIndex = -1;
    }
}

// Function to seek to a specific part of the song (for both mouse and touch)
function seekSong(index, event) {
    event.preventDefault(); // Prevent default touch behavior
    const audio = document.getElementById(`audio-${index}`);
    const playtimeBarContainer = event.currentTarget;

    // Handle both mouse and touch events
    const clientX = event.type.includes('touch') ? event.touches[0].clientX : event.clientX;
    const rect = playtimeBarContainer.getBoundingClientRect();
    const clickX = clientX - rect.left;
    const width = rect.width;
    const seekPercentage = clickX / width;

    audio.currentTime = seekPercentage * audio.duration;

    // Update the playtime bar immediately
    const playtimeBar = document.getElementById(`playtime-bar-${index}`);
    playtimeBar.style.width = `${seekPercentage * 100}%`;

    // Update the time display
    const timeDisplay = document.getElementById(`time-display-${index}`);
    const remainingTime = audio.duration - audio.currentTime;
    timeDisplay.textContent = formatTime(remainingTime);
}

// Function to adjust volume
function adjustVolume(index, volume) {
    const audio = document.getElementById(`audio-${index}`);
    const volumeBar = document.getElementById(`volume-bar-${index}`);
    audio.volume = volume;
    volumeBar.style.width = `${volume * 100}%`;
}

// Function to show lyrics in a pop-up
function showLyrics(index) {
    const song = songs[index];
    const lyricsPopup = document.createElement('div');
    lyricsPopup.classList.add('lyrics-popup');
    lyricsPopup.innerHTML = `
        <div class="lyrics-content">
            <h2>${song.name} - ${song.singer}</h2>
            <pre>${song.lyrics || "Lyrics not available yet."}</pre>
            <button onclick="this.parentElement.parentElement.remove()">Close</button>
        </div>
    `;
    document.body.appendChild(lyricsPopup);
}

// Function to toggle loop mode
function toggleLoop() {
    isLooping = !isLooping;
    isQueueing = false; // Disable queueing if looping is enabled
    const loopBtn = document.getElementById('loopBtn');
    const queueBtn = document.getElementById('queueBtn');
    loopBtn.classList.toggle('active', isLooping);
    queueBtn.classList.remove('active');
}

// Function to toggle queue mode
function toggleQueue() {
    isQueueing = !isQueueing;
    isLooping = false; // Disable looping if queueing is enabled
    const loopBtn = document.getElementById('loopBtn');
    const queueBtn = document.getElementById('queueBtn');
    queueBtn.classList.toggle('active', isQueueing);
    loopBtn.classList.remove('active');
}

// Function to search songs
function searchSongs() {
    const searchTerm = document.getElementById('searchInput').value.toLowerCase();
    const filteredSongs = originalSongList.filter(song =>
        song.name.toLowerCase().includes(searchTerm)
    );
    loadSongs(filteredSongs);
}

// Load songs when the page loads
document.addEventListener('DOMContentLoaded', () => {
    loadSongs();
});